catalog_script_client.script:
/* global onChange, g_form */
function onChange(control, oldValue, newValue, isLoading) {
  if (isLoading || newValue == '') {
    return;
  }
  if (newValue === "true") {
    var paramsWithValues = 0;
    var params = {};
    for (var i = 1; i <= 4; i++) {
      var paramName = 'param' + i;
      if (g_form.getValue(paramName) != '') {
        paramsWithValues++;
        params[paramsWithValues] = g_form.getValue(paramName);
      }
    }
    var method = g_form.getValue('method').split(':')[0];
    var functionStr = 'g_form.' + method;
    var functionParams = '';
    var result = '';
    try {
      if (paramsWithValues === 0) {
        result = g_form[method]();
      }
      if (paramsWithValues === 1) {
        functionParams = '("' + params['1'] + '")';
        result = g_form[method](params['1']);
      }
      if (paramsWithValues === 2) {
        functionParams = '("' + params['1'] + '","' + params['2'] + '");';
        result = g_form[method](params['1'], params['2']);
      }
      if (paramsWithValues === 3) {
        functionParams = '("' + params['1'] + '","' + params['2'] + '","' + params['3'] + '");';
        result = g_form[method](params['1'], params['2'], params['3']);
      }
      if (paramsWithValues === 4) {
        functionParams = '("' + params['1'] + '","' + params['2'] + '","' + params['3'] + '","' + params['4'] + '");';
        result = g_form[method](params['1'], params['2'], params['3'], params['4']);
      }
    } catch (error) {
      g_form.addErrorMessage(functionStr + functionParams + ' failed to execute.');
    }
    g_form.hideFieldMsg('function', true);
console.log("result", result);
    g_form.showFieldMsg('function', functionStr + functionParams + '// returns (also in console) "' + result + '"', 'info');
    g_form.setValue('tryit',false);
  }
}
